tasks.command:
bash -lc 'read -p "Domain: " DOMAIN; read -p "Admin email: " EMAIL; cat > .env <<
EOF
DOMAIN=${DOMAIN}
EMAIL=${EMAIL}
ADDR=${DOMAIN}
AUTO_HTTPS=
TLS_DIRECTIVE=tls ${EMAIL}
HSTS_LINE=Strict-Transport-Security "max-age=31536000; includeSubDomains; preload"
COMPOSE_PROJECT_NAME=loancalc
EOF
./deploy.sh --build'
